import React from 'react';
import FormEtapa1 from './components/FormEtapa1'
import FormEtapa2 from './components/FormEtapa2'
import FormEtapa3 from './components/FormEtapa3'
import Final from './components/Final'
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      etapaAtual: 1,
      ensinoMedioIncompleto: true,
      ensinoMedioIncompleto: true,
      ensinoMedioIncompleto: true,
      ensinoSuperiorCompleto: true
    }
  }



clickButtonContinue = () => {
  const novaEtapa = this.state.etapas;
  novaEtapa = novaEtapa === 4 ? (novaEtapa = 1) : ++novaEtapa;
  this.setState({ etapaAtual: novaEtapa})
}


mudandoValorOption = () => {
  if(ensinoMedioCompleto || ensinoMedioIncompleto)
    return <FormEtapa2/>
}

render() {  



  return (

     <div className="App">
        <FormEtapa1
        medioIncompleto={this.ensinoMedioIncompleto}
        medioCompleto={this.ensinoMedioCompleto}
        superiorIncompleto={this.ensinoSuperiorIncompleto}
        superiorIncompleto={this.ensinoSuperiorCompleto}
        onChangeValueOption={this.mudandoValorOption}
        />

        <button onClick={this.clickButtonContinue}>Continuar</button>

      </div>
    );
  }
}

export default App;
