    if(this.state.tipoDeEstudo === "medioCompleto"){
      this.setState({etapaAtual: false})
      this.setState({ensinoMedioCompleto: true})
  }
}


render() {  



  return (
    <div className="App">
        <FormEtapa1
          onChangeValueOption={this.mudandoValorOption}
          />
          {(this.state.etapaAtual)}
